function getSelectedTableFilters() {
    console.log('getSelectedTableFilters');
    var filters = [];
    for (var index in tableCols) {
        var selectedValues = $('select#' + tableCols[index]).val();
        if (selectedValues && selectedValues.length > 0) {
            filters[index] = selectedValues;
        }
    }
    return filters;
}


function filterData(filters) {
    console.log('filterData');
    var filteredRecords = {};

    if (filters) {
        for (var pub_key in _data['publication']) {
            var row = _data['publication'][pub_key];
            // Check filters
            var addToDisplay = true;
            var result = false;
            for (var i in filters) {
                var match = row[tableCols[i]];
                if (i == 3) {
                    collab_key = match;
                    match = _data['entity'][collab_key]['entity_name'];
                }
                if (filters[i] && filters[i].length > 0) {
                    var result = (filters[i].includes(match));
                    for (var j in filters[i]) {
                        if (match.includes(filters[i][j])) {
                            result = true;
                            break
                        }
                    }
                    if (result === false) {
                        addToDisplay = false;
                        break;
                    }
                }
            }
            if (addToDisplay) {
                var url = _data['publication'][pub_key]['downloadURL'];
                filteredRecords[pub_key] = [row, url];
            }
        }
    }
    return filteredRecords;
}

