function asc(s1, s2) {
    var s1lower = s1.toLowerCase();
    var s2lower = s2.toLowerCase();
    return s1lower > s2lower ? 1 : (s1lower < s2lower ? -1 : 0);
}

function desc(s1, s2) {
    var s1lower = s1.toLowerCase();
    var s2lower = s2.toLowerCase();
    return s1lower < s2lower ? 1 : (s1lower > s2lower ? -1 : 0);
}

function sortTable(col,dir){
    console.log(col);
    var rows = $('#livefeeds tbody  tr').removeClass('row_alt').get();
    var t=1;
    if(!dir || dir=='dec') {
        t=-1;
    } else if(dir=='asc') {
        t=1;
    }
    rows.sort(function(a, b) {
        var A = $(a).children('td').eq(col).text().toUpperCase();
        var B = $(b).children('td').eq(col).text().toUpperCase();
        if(A <b> B) {
            return t;
        }
        return 0;
    });
    $.each(rows, function(index, row) {
        $('#livefeeds').children('tbody').append(row);
        if( index%2 == 1){
            $(row).addClass('row_alt');
        }
    });
}

function sortIt(dropdownkey) {
    var id = '#' + dropdownkey;
    var theOptions = $(id + " option");
    if (theOptions.length > 0) {

        // var my_options = $("#my-dropdown option");
        theOptions.sort(function(a, b) {
            if (a.text > b.text) return 1;
            else if (a.text < b.text) return -1;
            else return 0
        })
        $(id).empty(); //.append(theOptions);
        $(id).chosen({ no_results_text: "No results matched" });
    }
}

function createFilterFor(key, index) {
    console.log('createFilterFor');
    var options = [];
    for (var pub in _data['publication']) {

        if (key == "entity_uploader") {
            var collab_key = _data['publication'][pub][key];
            var values = [_data['entity'][collab_key]['entity_name']];
        } else {
            var values = [_data['publication'][pub][key]];
        }

        if (values.length > 0) {
            for (var n in values) {
                var value = values[n];
                value = value.trim();

                // already in filter?
                if ($.inArray(value, options) === -1) {
                    options.push(value);
                }
            }
        }
    }

    options.sort(asc);

    var string = '<select id="' + key + '" class="chosen-select" multiple="' + options.length + '">';

    for (var i = 0; i < options.length; i++) {
        string += '<option value="' + options[i] + '">' + options[i] + '</option>';
    }
    string += '</select>';
    $("#options").append(string);

    var placeHolder = tableColumnTitles[index];

    $("#" + key).chosen({
        no_results_text: "Oops, nothing found!",
        width: "90%",
        allow_single_deselect: true,
        placeholder_text_multiple: placeHolder,
    });

}
